import { TypeAnimation } from 'react-type-animation';
import './App.css';

function App() {
  // const [ChatGPTMessage, setChatGPTMessage] = useState<string>('Waiting on value');
  const [ChatGPTMessage, setChatGPTMessage] = useState<string>("");
  const [value, setValue] = useState<string>("");

  async function submit(){
    let res = await fetch('http://localhost:8080/college-essay', {
      method: 'POST', 
      body: JSON.stringify({
        ChatGPTMessage: ChatGPTMessage, 
        b: "Data for ChatGPT"})})
        // .then((response) => response.json())
        // .then((data) => {setValue(data.body)});
        let resJson = await res.json();

  }



  useEffect(() => {}, []);

  return (

    <div className="App">
        <input type="textarea" 
          name="textValue"
          onChange={(e) => setChatGPTMessage(e.target.value)}
        />
        <button onClick={submit} >Send the data</button>
        <div>{value}</div>
        <br></br>
        <TypeAnimation 
          sequence={[
            'This will be the typing animation of the response, eventually.',
          ]}
          speed={70}
          wrapper="div"
        />

    </div>
  );
}

export default App;
